import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { GET_MENUS, SEARCH_MENUS } from './queries';

const MenuTable = () => {
  const [sortByPrice, setSortByPrice] = useState(false);
  const [searchTerm, setSearchTerm] = useState(''); 
  const { loading, error, data } = useQuery(searchTerm ? SEARCH_MENUS : GET_MENUS, {
    variables: searchTerm ? { name: searchTerm } : { sortByPrice }, // Use SEARCH_MENUS if searchTerm is not empty
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const menus = data?.menus || data?.searchMenus; // Use optional chaining

  const toggleSort = () => {
    setSortByPrice((prevSortByPrice) => !prevSortByPrice);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Search by Name"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <button onClick={toggleSort}>
          {sortByPrice ? 'Sort by Name' : 'Sort by Price'}
        </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
        {menus?.map((menu) => ( // Use optional chaining
            <tr key={menu.id}>
              <td>{menu.id}</td>
              <td>{menu.name}</td>
              <td>{menu.price}</td>
              <td>{menu.createdAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MenuTable;
