import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { GET_MENUS, SEARCH_MENUS } from './queries';
import './css/MenuTable.css'; //

const MenuTable = () => {
  const [sortByPrice, setSortByPrice] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const { loading, error, data } = useQuery(searchTerm ? SEARCH_MENUS : GET_MENUS, {
    variables: searchTerm ? { name: searchTerm } : { sortByPrice },
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  // Convert the data to a plain object before rendering it
  const menus = JSON.parse(JSON.stringify(data?.menus || data?.searchMenus));

  const toggleSort = () => {
    setSortByPrice((prevSortByPrice) => !prevSortByPrice);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div>
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Search by Name"
          value={searchTerm}
          onComplete={handleSearchChange}
          onChange={handleSearchChange}
        />
        <button className="search-button" onClick={toggleSort}>
          {sortByPrice ? 'Unsort' : 'Sort by Price'}
        </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {menus?.map((menu) => (
            <tr key={menu.id}>
              <td>{menu.id}</td>
              <td>{menu.name}</td>
              <td>{menu.price}</td>
              <td>{menu.createdAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MenuTable;
