import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { GET_MENUS } from './queries';

const MenuTable = () => {
  const [sortByPrice, setSortByPrice] = useState(false);
  const { loading, error, data } = useQuery(GET_MENUS, {
    variables: { sortByPrice },
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const menus = data.menus;

  const toggleSort = () => {
    setSortByPrice((prevSortByPrice) => !prevSortByPrice);
  };

  return (
    <div>
      <button onClick={toggleSort}>
        {sortByPrice ? 'Sort by Name' : 'Sort by Price'}
      </button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {menus.map((menu) => (
            <tr key={menu.id}>
              <td>{menu.id}</td>
              <td>{menu.name}</td>
              <td>{menu.price}</td>
              <td>{menu.createdAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MenuTable;
