import React, { useState, useRef, useEffect } from 'react';
import { useQuery } from '@apollo/client';
import { GET_MENUS, SEARCH_MENUS } from './queries'; // Import SEARCH_MENUS query

const MenuTable = () => {
  const [sortByPrice, setSortByPrice] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const inputRef = useRef(null); // Ref for the input element

  const { loading, error, data } = useQuery(searchTerm ? SEARCH_MENUS : GET_MENUS, {
    variables: searchTerm ? { name: searchTerm } : { sortByPrice },
  });

  useEffect(() => {
    // Set focus on the input element when the component re-renders
    inputRef.current.focus();
  }, [searchTerm]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const menus = data?.menus || data?.searchMenus;

  const toggleSort = () => {
    setSortByPrice((prevSortByPrice) => !prevSortByPrice);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Search by Name"
          value={searchTerm}
          onChange={handleSearchChange}
          ref={inputRef} // Assign the ref to the input element
        />
        <button onClick={toggleSort}>
          {sortByPrice ? 'Sort by Name' : 'Sort by Price'}
        </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>

        </tbody>
      </table>
    </div>
  );
};

export default MenuTable;
