import React, { useState } from 'react';
import { useLazyQuery } from '@apollo/client';
import { SEARCH_MENUS } from './queries';

const MenuTable = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [getMenus, { loading, error, data }] = useLazyQuery(SEARCH_MENUS);

  const handleSearch = () => {
    getMenus({ variables: { name: searchQuery } });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const menus = data ? data.searchMenus : [];

  return (
    <div>
      <input
        type="text"
        placeholder="Search by Name"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {menus.map((menu) => (
            <tr key={menu.id}>
              <td>{menu.id}</td>
              <td>{menu.name}</td>
              <td>{menu.price}</td>
              <td>{menu.createdAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MenuTable;
